/* eslint-disable react-hooks/exhaustive-deps */
import React, { useCallback } from 'react';
import { useSharedValue, withTiming, useAnimatedStyle } from 'react-native-reanimated';
import { Colors } from "../../style";
import View from "../../components/view";
const DEFAULT_OVERLAY_BACKGROUND_COLORS = Colors.rgba(Colors.grey20, 0.65);
const useFadeView = props => {
  const {
    visible,
    overlayBackgroundColor = DEFAULT_OVERLAY_BACKGROUND_COLORS,
    testID
  } = props;
  const fadeOpacity = useSharedValue(visible ? 1 : 0);
  const hideNow = useCallback(() => {
    fadeOpacity.value = 0;
  }, []);
  const fade = useCallback(type => {
    'worklet';

    const newValue = type === 'enter' ? 1 : 0;
    if (fadeOpacity.value !== newValue) {
      fadeOpacity.value = newValue;
    }
  }, []);
  const fadeStyle = useAnimatedStyle(() => {
    return {
      opacity: withTiming(fadeOpacity.value, {
        duration: 300
      }),
      backgroundColor: overlayBackgroundColor
    };
  }, [overlayBackgroundColor]);
  const FadeView = <View testID={testID} absF reanimated style={fadeStyle} pointerEvents="none" />;
  return {
    FadeView,
    hideNow,
    fade
  };
};
export default useFadeView;
