    dismiss: animateOut
  }));
  const renderDialog = () => {
    return <PanGestureHandler onGestureEvent={_isEmpty(directions) ? undefined : panGestureEvent}>
        <View {...containerProps} reanimated style={style} onLayout={onLayout} ref={setRef} testID={testID}>
          {headerProps && <DialogHeader {...headerProps} />}
          {children}
        </View>
      </PanGestureHandler>;
  };
  return <Modal transparent animationType={'none'} {...otherModalProps} testID={`${testID}.modal`} useGestureHandlerRootView visible={visible} onBackgroundPress={ignoreBackgroundPress ? undefined : close} onRequestClose={ignoreBackgroundPress ? undefined : close} onDismiss={undefined}>
      {FadeView}
      <View useSafeArea={useSafeArea} pointerEvents={'box-none'} style={alignmentStyle}>
        {renderDialog()}
      </View>
